  notes_pt
) VALUES (
  -- Identity
  NULL,
  'Besouro Gago',
  'Periperi',
  NULL, -- No documented title
  NULL, -- No portrait available
  ARRAY['https://grupoliberdadecapoeira.com.br/linhagem-mestres/']::text[],
  -- Capoeira-specific
  'angola'::genealogy.style,
