WHERE s.apelido = 'Zé Maria' AND o.apelido = 'Paulinho Salmon'
ON CONFLICT (subject_type, subject_id, predicate, object_type, object_id, COALESCE(started_at, '0001-01-01'::date)) DO NOTHING;

