  -- Identity
  'Jovino Pedro de Alcântara',
  'Jovino dos Coelhos',
  NULL,
  NULL,
  ARRAY['https://www.academia.edu/7186292/Capoeira_e_capoeiras_entre_a_Guarda_Negra_e_a_Educação_Física_no_Recife']::text[],
  -- Capoeira-specific
