  notes_pt
) VALUES (
  -- Identity
  NULL,
  'Valdevino',
  NULL,
  NULL,
  ARRAY['http://www.ofrevo.com/2014/01/valdemar-de-oliveira-frevo-capoeira-e.html']::text[],
  -- Capoeira-specific
